import React, {Component} from 'react';

class MovieApp extends Component {
    state = {
        currentid: 0,
        searchTerm: "",
        addTitle: "",
        addYear: 0,
        addPoster: "",
        addDescription: "",
        currentMovie: null,
        movieList: [],
    };

    fetchMovies = () => {
        fetch('http://localhost:8080/movies/',{
            'Content-Type': 'application/json',
            'Accept': 'application/json'
        })
        .then((response) => response.json())
        .then((response) => {
            this.setState({movieList: response})
        });
    }

    componentDidMount() {
        this.fetchMovies();
    }

    setMovie = (e) => {
        this.setState({currentMovie: this.state.movieList[Number(e.target.id)]})
    }

    showMovie = () => {
        if(this.state.currentMovie){
            return (
                <div className="card">
                    <div class="movie-title">{this.state.currentMovie && this.state.currentMovie.title} {this.state.currentMovie && this.state.currentMovie.year}</div>
                    <div className="card-content">
                        <img class="movie-poster" src={this.state.currentMovie && this.state.currentMovie.poster}/>
                        <p>
                            <hr/>
                            <h4>Description: </h4>
                            <div className='movie-description'>
                                {this.state.currentMovie && this.state.currentMovie.description}
                            </div>
                        </p>
                    </div>
                </div>
            );
        }
        else{
            return (
                <div className="card">
                    <div className="card-content">
                        <div>No movie selected</div>
                    </div>
                </div>
            )
        }
    }

    getMovies = () => {
        if(this.state.movieList.length < 0){
            return(
                <div>No movies found</div>
            );
        }
        else{
            return(
                this.state.movieList.map((movie,i) => {
                    return(
                        <div>
                            <a onClick={this.setMovie} id={i} href="#">{movie.title} ({movie.year})</a>
                        </div>
                    )
                })
            );
        }
    }

    addMovie = () => {

    }
    
    render() {
        return (
            <div className="App-body">
                <div className="leftside">
                <div>
                    <form className="card">
                    <div className='card-title'>
                        Search Movie:
                    </div>
                    <div className='card-content'>
                        <div className='form-divider'>
                        <label>
                            Title: <input type="text"/>
                        </label>
                        <button className='form-button'>
                            Search
                        </button>
                        </div>
                    </div>
                    </form>
                    <form className="card">
                    <div className='card-title'>
                        Add Movie:
                    </div>
                    <div className='card-content'>
                        <div className='form-divider'>
                        <div>
                            <label>
                            Title: <input type="text"/>
                            </label><br/>
                            <label>
                            Year: <input type="number"/>
                            </label>
                        </div>
                        <div className='empty'></div>
                        <div>
                            <label>
                            Poster URL: <input type="text"/>
                            </label><br/>
                            <label>
                            Description: <input type="text"/>
                            </label>
                        </div>
                        <div className='form-button'>
                            <br/>
                            <button>
                            Add
                            </button>
                        </div>
                        </div>
                    </div>
                </form>
                </div>
                <div className='card'>
                    <div className='card-title'>
                        Movies:
                    </div>
                    <div className='card-content'>    
                        {this.getMovies()}
                    </div>
                </div>
                </div>
                <div className="rightside">
                    {this.showMovie}
                </div>
            </div>
        );
    }
}

export default MovieApp;
