import React, {Component} from 'react';

class MovieApp extends Component {
    state = {
        currentid: 0,
        searchTerm: "",
        addTitle: "",
        addYear: 0,
        addPoster: "",
        addDescription: "",
        currentMovie: {id: 0, title: "Interstellar", year: 2014, poster:'https://i.ytimg.com/vi/YF1eYbfbH5k/maxresdefault.jpg', description: "Man goes to space to save the world."},
        movieList: [
            {id: 0, title: "Interstellar", year: 2014, poster:'https://i.ytimg.com/vi/YF1eYbfbH5k/maxresdefault.jpg', description: "Man goes to space to save the world."},
            {id: 1, title: "Dune", year: 2021, poster:'https://assets-prd.ignimgs.com/2021/08/09/dune-button-2021-1628542173776.jpg', description: "Paul Atreites arrives on Arrakis for a big surprise"},
        ],
    };

    setMovie = (e) => {
        console.log("here is the movie list");
        console.log(this.state.movieList[Number(e.target.id)]);
        this.setState({currentMovie: this.state.movieList[Number(e.target.id)]})
    }

    showMovie = () => {
        if(this.state.currentMovie){
            return (
                <div>
                    <h3 id="movieTitle">{this.state.currentMovie && this.state.currentMovie.title}</h3>
                    <img class="movie-poster" src={this.state.currentMovie && this.state.currentMovie.poster}/>
                    <p>{this.state.currentMovie && this.state.currentMovie.description}</p>
                </div>
            );
        }
        else{
            return (
                <div>No movie selected</div>
            )
        }
    }

    getMovies() {
        console.log(this.state.movieList);
        if(this.state.movieList.length < 0){
            return(
                <div>No movies found</div>
            );
        }
        else{
            return(
                this.state.movieList.map((movie,i) => {
                    console.log(this.state.movieList[i]);
                    return(
                        <div onClick={this.setMovie} id={i}>
                            <pre>JMDBid:{movie.id}</pre>
                            -   {movie.title} ({movie.year})
                        </div>
                    )
                })
            );
        }
    }
    
    render() {
        return (
            <div className="App-body">
                <div className="leftside">
                <div>
                    <form className="card">
                    <div className='card-content'>
                        <div>
                        Search Movie:
                        </div>
                        <div className='form-divider'>
                        <label>
                            Title: <input type="text"/>
                        </label>
                        <button className='form-button'>
                            Search
                        </button>
                        </div>
                    </div>
                    </form>
                    <form className="card">
                    <div className='card-content'>
                        <div>
                        Add Movie:
                        </div>
                        <div className='form-divider'>
                        <div>
                            <label>
                            Title: <input type="text"/>
                            </label><br/>
                            <label>
                            Year: <input type="number"/>
                            </label>
                        </div>
                        <div className='empty'></div>
                        <div>
                            <label>
                            Poster URL: <input type="text"/>
                            </label><br/>
                            <label>
                            Description: <input type="text"/>
                            </label>
                        </div>
                        <div className='form-button'>
                            <br/>
                            <button>
                            Add
                            </button>
                        </div>
                        </div>
                    </div>
                    </form>
                </div>
                <div className='card'>
                    <div className='card-content'>
                    Movies:
                    {this.getMovies()}
                    </div>
                </div>
                </div>
                <div className="rightside">
                    <div className="card">
                        <div className="card-content">
                            {this.showMovie()}
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default MovieApp;
